mapper.select:
select
    article.aid, article.uid, article.title, article.create_time, article.update_time, article.watch, article.likee, article.status, article.def_flag
from article
    join link_article_tag on link_article_tag.aid = article.aid
    join tag              on link_article_tag.tid = tag.tid


        tag.title = #{tag}


        and article.uid = #{uid}


        and article.create_time >= #{beginTime}


        and article.create_time <= #{endTime}



    order by ${order} desc
